import React, { Component } from "react";
import axios from "axios";

class App extends Component {

  state = {
    articles: []
  };

  componentDidMount() {
    this.getAllArticles()
  }

  getAllArticles = () => {
    axios.get('/articles')
      .then(res => {
        if(res.data){
          this.setState({
            todos: res.data
          })
        }
      })
      .catch(err => console.log(err))
  };

  render() {
    return (
      <div>
